import React, {Component} from 'react';
import {Link, Route, Switch, Redirect} from 'react-router-dom';
import Home from '../../views/exercise2/home';
import Explore from '../../views/exercise2/explore';
import Login from '../../views/exercise2/login';
import fakeAuth from '../../views/exercise2/login';


let style = {
    color: 'white', 
    marginTop: 30, 
    textDecoration: 'none'
};

const homeLinkStyle = {
    color: "rgb(4, 92, 92)", 
    textDecoration: "none", 
    fontWeight: 'bold'
};

const peekLinkStyle = {
    color: "rgb(4, 92, 92)", 
    textDecoration: "none", 
    fontWeight: 'bold',
    marginLeft: 20
};

const PrivateRoute = ({component: Component, authed, ...rest}) => {
    return (
      <Route
        {...rest}
        render={(props) => authed === true
          ? <Component {...props} />
          : <Redirect to={{pathname: '/explore', state: {from: props.location}}} />} />
    )
}

class CernApp extends Component {
    constructor(props) {
        super(props);

        this.state = {visibility: true};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {

        // if(this.state.visibility)
        //     style.visibility = "visible";
        // else
        //     style.visibility = "hidden";

        this.setState({visibility: false});



    }

    render() {
        return (
            <div>

                <div style= {{backgroundColor: "white", width: 150, padding: 5}}>
                    <Link to= "/" style= {homeLinkStyle}>Home</Link>
                    <Link to= "/explore" style= {peekLinkStyle}>Explore</Link>
                </div>

                <Switch>
                    <Route exact path= "/" component= {Home} />
                    <Route path= "/login" component= {Login}/>
                    <PrivateRoute authed={fakeAuth.isAuthenticated} path='/explore' component = {Explore} />
                </Switch>

            </div>
        );
    }
}

export default CernApp;

