import React, {Component} from 'react';
import {Link, Route, Switch, Redirect} from 'react-router-dom';
import Home from '../../views/exercise2/home';
import Peek from '../../views/exercise2/peek';

let style = {
    color: 'white', 
    marginTop: 30, 
    textDecoration: 'none', 
    position: "fixed", 
    bottom: 40, 
    right: 450,
    visibility: 'visible'
}

class CernApp extends Component {
    constructor(props) {
        super(props);

        this.state = {visibility: true};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState({visibility: false});

        if(this.state.visibility)
            style.visibility = "visible";
        else
            style.visibility = "visible";

    }

    render() {
        return (
            <div>

                <Link to= "/"></Link>
                <Link to= "/peek" style= {style} onClick= {this.handleClick}> >> Allow me guide you explore the beauty of Nigeria >></Link>

                <Switch>
                    <Route exact path= "/" component= {Home} />
                    <Route path= "/peek" component= {Peek}/>
                </Switch>

            </div>
        );
    }
}

export default CernApp;
