import React, {Component} from 'react';
import {Link, Route, Switch, Redirect} from 'react-router-dom';
import Home from '../../views/exercise2/home';
import Peek from '../../views/exercise2/peek';

const style = {
    color: 'white', 
    marginTop: 30, 
    textDecoration: 'none', 
    position: "fixed", 
    bottom: 0, 
    right: 100
}

class CernApp extends Component {
    constructor(props) {
        super(props);

    }

    render() {
        return (
            <div>

                <Link to= "/"></Link>
                <Link to= "/peek" style= {style}> >> Allow me guide you explore the beauty of Nigeria >></Link>

                <Switch>
                    <Route exact path= "/" component= {Home} />
                    <Route path= "/peek" component= {Peek}/>
                </Switch>

            </div>
        );
    }
}

export default CernApp;
