import React from 'react';
import details from './details';

let placeArr = [];
const StateDetails = (props) =>{
    details.map((data) =>{
        if(data.name === props.name){

            data.places.forEach((place, index) =>{
                placeArr[index] = <li>{place}</li>;
            })

        }

    })




    let state = details.map((data) =>{
        if(data.name === props.name)
            return(
                <div style={{textAlign: 'center', marginTop: 40}}>
                    <img src = {require(`../../images/${data.image}`)} style = {{height: '25%', width: '40%',}} />
                    <ul style= {{textAlign: 'left', color: 'white', fontFamily: 'cursive', marginLeft: 450}}>
                        {placeArr}
                    </ul>

                </div>
            );
    });

    return (
        <div>{state}</div>
    );
}

export default StateDetails;

