import React from 'react';

const details = [
    {
        name: "Abia",
        places: [
            "Arochukwu Caves",
            "Enemabia warm spring",
            "National War Museum Monument Umuahia",
            "Akwette Weaving Centre Arts and Crafts",
            "Long juju of Arochukwu Cultural festivals",
            "Azumini Bule River",
            "Museum of Colonial History Monument"
        ],
        image: "../../images/abuja.jpg"
    },
    {
        name: "Adamawa",
        places: [

            "Modibbo Adam’s Tomb Monument",
            "Three Sister Rocks Monument",
            "Mandara Mountains",
            "Kiri Dam",
            "Sassa Water falls at Toungo & Yadim Water falls",
            "Mandara Mountains",
            "Koma Hills",
            "Elephant House", 
            "Gashaka Gumti National Park, Eco-Tourism (Wildlife)",
            "Sukur (UNESCO World Monument Heritage Site)",
            "Lamurde Hot Spring"

        ],
        image: "../../images/adamawa.jpg"
    },
];

const StateDetails = (props) =>{
    let state = details.map((data) =>{
        return(
            <img src = {require(data.image)} style = {{height: '32%', width: '53%'}} />
        );
    });

    return (
        <div>{state}</div>
    );
}

export default StateDetails;

