import React, {Component} from 'react';
import ReactDOM from 'react-dom';
//import './index.css';
import MyApp from './MyApp';
import {Text} from './views/Text';
import {Head} from './views/Head';
import {Counter} from './views/Counter';
import {DecreaseButton} from './views/Button'
import {IncreaseButton} from './views/Button';
import * as serviceWorker from './serviceWorker';

class MyContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {counter: 0};

        this.increaseCounter = this.increaseCounter.bind(this);
        this.decreaseCounter = this.decreaseCounter.bind(this);
    }

    increaseCounter() {
        this.setState({counter: this.state.counter + 1});
    }

    decreaseCounter() {
        let count = this.state.counter;
        if(count > 0) {
            this.setState({counter: count - 1});
        }
    }

    render() {
        return (
            <div style = {{backgroundColor: 'rgb(4, 92, 92'}}>
                <div style = {{textAlign: 'center', marginBottom: 20}}> <Head /> </div>
                <div> <Text /> </div>
                <div> <Counter counter = {this.state.counter} /> </div>
                <div style = {{textAlign: 'center', marginBottom: 30}}>
                    <IncreaseButton onClick = {this.increaseCounter}/>
                    <DecreaseButton onClick = {this.decreaseCounter}/>
                </div>
            </div>
        );
    }
}

ReactDOM.render(
    <MyContainer />,
    document.getElementById('root')
);

//ReactDOM.render(<MyApp />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
